sp_widget.script:
(function() {
	if(gs.hasRole('itil') || gs.hasRole('support')){
		data.showicons=true;
	}

	data.showBoldChat = true;
	data.ShowMultiSwith = false;
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	var myuser = gs.getUser().getID();
	var gu = new GlideRecord("sys_user");
	gu.addQuery('sys_id', myuser);
	gu.query();
		while (gu.next())
		{
			data.usrComSysID = gu.getValue('company');
				//gs.addErrorMessage(data.usrComSysID);
		}



	var id = myuser.getCompanyID();
//gs.addInfoMessage(data.usrComSysID);
	//data.usrComSysID = myuser;
	//data.usrComSysID = gs.getUser().getRecord().getValue("company.name");
	var gr = new GlideRecord("core_company");
	gr.addQuery("sys_id", data.usrComSysID);
	gr.query();
	while (gr.next())
		{
			var validateImage = gr.getDisplayValue("banner_image");
			if(validateImage){
				data.ifValid = true;
	data.imgURL = "https://unisysmtdev.service-now.com/" + gr.getDisplayValue("banner_image");
	//gr.('data.imgURL' + "usrComSysID");
		}
	else{
		data.ifValid = false;
	}
		}

	var mps = new GlideRecord("u_sm_multi_portal_select");
	mps.addQuery("u_company", data.usrComSysID);
	mps.query();
	if(mps.next())
		{

			var link1 = mps.getValue("u_link1_url");
				if (link1){
					data.isLink1=true;
					data.link1Name = mps.getDisplayValue("u_link1_name");
					data.link1URL = mps.getDisplayValue("u_link1_url");
					data.link1Role = mps.getDisplayValue("u_link1_role");
				}
			var link2 = mps.getValue("u_link2_url");
		   // gs.addInfoMessage("Before: Link2: "+link2);
				if (link2){
					data.isLink2=true;
					data.link2Name = mps.getDisplayValue("u_link2_name");
					data.link2URL = mps.getDisplayValue("u_link2_url");

				}


			if((data.isLink1==true)||(data.isLink2==true))
				data.ShowMultiSwith = true;
			//data.usrComName = mps.getValue("u_company");
			//gs.addErrorMessage("MultiCompany Record Found" +data.usrComName);
		}
	else
	{
		//gs.addInfoMessage("MultiCompany Record NOT Found..!");
	}

	var myUserObject = gs.getUser();
	data.userName = myUserObject.getFullName();
	data.userEmail = myUserObject.getEmail();
	data.mobile = myUserObject.getMobileNumber();
	data.BCID = "";
	if (data.usrComSysID == "8ddb3ed337560780ddf983dcb3990ecf" )
	{
		//gs.addErrorMessage("Nutreco");
		data.BCID = "3763286451440643626";
	}
	else if (data.usrComSysID == "60d6cff70fea0380ffd5ad7d51050eec" )
		{
			//gs.addErrorMessage("Constellium");
			data.BCID = "2727972173764701495";
		}
	else if (data.usrComSysID == "97abbf6d0f074200ac0e22d8b1050ebb")
		{
			//gs.addErrorMessage("Unisys");
			data.BCID = "852431632631040349";

		}
	else if (data.usrComSysID == "83028d04f5cfa00d35a0f5d0210c789" )
		{
			//gs.addErrorMessage("Unisys");
			data.BCID = "852431632631040349";

		}

	else{
		//gs.addErrorMessage("Not one of Nutreco/Constellium");
		data.BCID = "852431632631040349";
	}


})();
